<?php

use Worksome\Number\Parser;

test('floats can be converted to string', function (float $input, string $expect) {
    $result = Parser::floatToString($input);

    expect($result)->toBe($expect);
})->with([
    [-1.0, '-1'],
    [0.0, '0'],
    [1.0, '1'],
    [1.2, '1.2'],
    [1.03, '1.03'],
    [1.004, '1.004'],
    [9834.239472, '9834.239472'],
    [PHP_FLOAT_MAX, ''],
    [PHP_FLOAT_MIN, '0.'],
    [-PHP_FLOAT_MAX, '-'],
    [-PHP_FLOAT_MIN, '-0.'],
]);
